    public static class MailHelper
    {
        public static async Task sendMail(string email, Event @event)
        {
            using var emailMessage = new MimeMessage();

            emailMessage.From.Add(new MailboxAddress("Аудиторный фонд ВЭПИ", "maxim-podtynnikov@yandex.ru"));
            emailMessage.To.Add(new MailboxAddress("", email));
            emailMessage.Subject = "Отмена мероприятия";
            emailMessage.Body = new TextPart(MimeKit.Text.TextFormat.Html)
            {
                Text = mailText($"Ваше мероприятие в кабинете {@event.classroom} от {@event.date} {@event.getLesson()} было отменено.") 
            };

            using (var client = new SmtpClient())
            {
                await client.ConnectAsync("smtp.yandex.ru", 465, true);
                await client.AuthenticateAsync("maxim-podtynnikov@yandex.ru", "sfjxubzdvowqhixa");
                await client.SendAsync(emailMessage);

                await client.DisconnectAsync(true);
            }
        }
